import React from 'react'

function StackOp(props) {
    const {gr,setGr,ch,st} = props

    const pushValue = (e) => {
        e.preventDefault()
        const value = document.getElementById("input1").value



        const myStruc = (st.find(e => e.id === ch.id).tile) //array of coordinates
        const top =-1
        for(let i =0;i<myStruc.length;i++){
            let {row,col} = myStruc[i]
            if(grid[row][col].text === ``){
                top = i
                break
            }
        }

        if(top > -1){
            const newGrid = gr.slice()
            const {r,c} = myStruc[top]
            const tile = newGrid[r][c] 
            const newTile= {
                ...tile,
                text:`${value}`

                }
            newGrid[r][c] = newTile
            setGr(newGrid)
        }


    } 
    return (
        <div className = "controls" >
                <form onSubmit = {pushValue}>
                    <input id="input1" type="text" placeholder="value" />
                    <button>Push</button>

                </form>

                <form onSubmit = {popValue}>
                    <input id = "input2" type="number" placeholder="index" />
                    <button>Pop</button>

                </form>
                <div>
                    see items
                </div>
        </div> 
    )
}

export default StackOp
