import React, {useState, useEffect} from 'react'
import Tile from './Tile'

function Grid(props) {
    const [grid,setGrid] = useState([])
    const [curStruc,setCurStruc] = useState([]) //all current structures in the grid
    const [chosen,setChosen] = useState([]) //all blocks currently clicked/hovered
    const [press, setPress] = useState(false) //is mouse click being held
    const [counter,setCounter] = useState(1) //generate new id

    const {op,setOp} = props

    const type = {
        ARRAY: "array",
        STACK: "stack",
        QUEUE: "queue",
        MAP: "map",
        TREE:"tree",
        GRAPH:"graph"
    }


    useEffect(() => {
        const newGrid = initializeGrid()
        setGrid(newGrid)
    },[])


    return (
        <div className ="myGrid">

            {grid.map((row,rId) =>(
                 <div className = "myRow" key={rId}>
                     {row.map((tile,tId) =>(
                        <Tile
                         key ={tId} 
                         col = {tile.col}
                         row = {tile.row}
                         sType = {tile.strType}
                         sId = {tile.strId}
                         st = {curStruc}
                         setSt = {setCurStruc}
                         pr = {press}
                         setPr = {setPress}
                         gr = {grid}
                         setGr = {setGrid}
                         isP ={tile.isPicked}
                         op = {op}
                         setOp ={setOp}
                         type ={type}
                         count = {counter}

                         />

                     ))}
                 </div>
            ))}

        </div>
    )
}

export default Grid

const initializeGrid = () => {
    const grid = []
    for(let row = 0; row <20;row++){
        const aRow = []
        for(let col = 0; col<45;col++){
            aRow.push(createTile(col,row))
        }
        grid.push(aRow)
    }
    return grid
}

const createTile = (col,row) => {
    return{
        col,
        row,
        strType: null,
        strId: 0,
        isPicked: false,

    }
}
