import React from 'react'

function Tile(props) {
    const {col,row,sType,sId,st,setSt,ch,setCh,gr,setGr,isP,op,setOp,type,count,setCount,txt} = props


    const stackGrid = ( size) => {
        if((row -size) >= -1){

        const newGrid = gr.slice();
        let tile,newTile,arow,coor =[]
        for(let i =0;i<size;i++){
            arow =row-i
            tile = newGrid[arow][col];
            newTile= {
              ...tile,
              isPicked: !isP,
              strType: type.STACK,
              strId:count
            };

            newGrid[arow][col] = newTile
            coor.push([arow,col])
        }
        setGr(newGrid)
        let newCount = count +1;
        setCount(newCount)

        const newSt = st.slice() //copy list of data structues
        newSt.push({id: count, tile: coor})
        setSt(newSt)
        }
      }

    const arrayGrid = (size) =>{
        //setOp("")
        if((col +size) <= 45){

            const newGrid = gr.slice()
            let tile, newTile,acol,coor =[]


            for(let i =0;i<size;i++){
                tile = newGrid[row][col+i]
                newTile= {
                ...tile,
                isPicked: !isP,
                strType: type.ARRAY,
                strId:count
                };

                newGrid[row][col+i] = newTile
                acol =col+i
                coor.push([row,acol])
            }
            setGr(newGrid)
            let newCount = count +1;
            setCount(newCount)

            const newSt = st.slice() //copy list of data structues
            newSt.push({id: count, tile: coor})
            setSt(newSt)
            console.log(st)
        }

    }

    const queueGrid = (size) => {
        if((col -size) >= -1){

            const newGrid = gr.slice();
            let tile, newTile,acol,coor =[]
            for(let i =0;i<size;i++){
                acol = col-i
                tile = newGrid[row][acol];
                newTile= {
                ...tile,
                isPicked: !isP,
                strType: type.QUEUE,
                strId:count

                };

                newGrid[row][acol] = newTile;
                coor.push([row,acol])
            }
            setGr(newGrid)
            let newCount = count +1;
            setCount(newCount)

            const newSt = st.slice() //copy list of data structues
            newSt.push({id: count, tile: coor})
            setSt(newSt)
        }
    }
    const mapGrid = (size) => {
        if((col +2) <= 44  && (row +size) <= 20){
            const newGrid = gr.slice();
            let tile, newTile,coor =[],arr,r,c
            for(let i = 0; i<size;i++){
                arr =[]
                for(let j = 0;j<3; j++){
                    r=row+i
                    c=col+j
                    tile = newGrid[r][c]
                    newTile= {
                    ...tile,
                    isPicked: !isP,
                    strType: type.MAP, 
                    strId:count

                    }
                newGrid[r][c] = newTile; //update tile in copy grid

                arr.push([r,c]) //pushes for single key/value


                }
                coor.push(arr)
            }
            setGr(newGrid)
            let newCount = count +1;
            setCount(newCount)

            const newSt = st.slice() //copy list of data structues
            newSt.push({id: count, tile: coor})
            setSt(newSt)

        }
    }

    const treeGrid = (root) =>{
        //setOp("")

            console.log(root)
            const newGrid = gr.slice()
            let tile, newTile,coor =[]

            // create  root tile
            tile = newGrid[row][col]
            newTile= {
            ...tile,
            isPicked: !isP,
            txt:`${root}`,
            strType: type.TREE,
            strId:count
            };

            newGrid[row][col] = newTile
            coor.push([row,col])

            setGr(newGrid)
            let newCount = count +1;
            setCount(newCount)
            const newSt = st.slice() //copy list of data structues
            newSt.push({id: count, tile: coor})
            setSt(newSt)
            /*
            //create children tiles
            let offset = [4,4,2,2,2,2,1,1,1,1,1,1,1,1],neg =-1


            for(let i =0;i<7;i++){
                [pRow,pCol] = coor[i]
                for(let j = 1; j<3 ;j++){
                    pRow +=1
                    pCol = pCol +offset[i]*neg
                    tile = newGrid[pRow ][pCol ]
                    newTile= {
                        ...tile,
                        isPicked: !isP,
                        strType: type.TREE,
                        strId:count
                    };
                    neg *= -1
                    newGrid[pRow][pCol ] = newTile
                    coor.push([pRow,pCol])
                }



            }
            setGr(newGrid)
            let newCount = count +1;
            setCount(newCount)
            console.log(coor)
            const newSt = st.slice() //copy list of data structues
            newSt.push({id: count, tile: coor})
            setSt(newSt)
            console.log(st)

            */
    }




    const click =() =>{
        if(sId ===0){
            if(op !== ""){
                let size= parseInt(prompt("Size/root:"),10)
                console.log(size)
                if(op === "Array")
                    arrayGrid(size)
                else if(op === "Stack")
                    stackGrid(size)
                else if(op === "Queue")
                    queueGrid(size)
                else if(op === "HashMap")
                    mapGrid(size)
                else if(op === "Tree")
                    treeGrid(size)
                else if(op === "Graph")
                    console.log("nothing yet")
            }
        }
        else{
            setCh({id:sId , type:sType})
            setOp(sType)
            console.log(ch)

        }
    }


    sId===ch.id ? console.log("YES"):console.log("NO")
    const extra = sId === ch.id ? ' highlight' : ''
    const main = sType === ``? ``:
                sType ===type.ARRAY ? `array helper` :
                sType ===type.STACK ? `stack helper` :
                sType ===type.QUEUE ? `queue helper` :
                sType ===type.MAP ? `map helper` :
                sType ===type.TREE ? `tree helper` :`graph helper` 
    return (
        <div className = {` myTile ${main} ${extra}`} 
            onClick = {() => click()} >
                {txt}
        </div>
    )
}

export default Tile
