        e.preventDefault()
        let value = document.getElementById("input1").value
        value = parseInt(value,10)

        //reinsert coordinates of tree in each add



        const fullStruc = (st.find(e => e.id === ch.id))
        let myStruc = fullStruc.tile //array of coordinates
