import React from 'react'

function Tile(props) {
    const {col,row,sType,sId,ch,setCh,pr,setPr} = props
    //console.log(col +" "+ row+" "+sType+ " "+sId)

    const mouseDown = (aRow,aCol) => {
        const newAr = ch.slice()
        newAr.push({aRow,aCol})
        setCh(newAr)
        setPr(true)
    }

    const mouseLift = () => {
        setPr(false)
        console.log(ch)
    }

    const mouseEnter =(aRow,aCol)=> {

        if(!pr) return 
        const newAr = ch.slice()
        newAr.push({aRow,aCol})
        setCh(newAr)
    }
    const extraCss = {}

    return (
        <div className = "myTile" 
             onMouseDown = {() => mouseDown(row,col)}
             onMouseUp = {() => mouseLift()}
             onMouseEnter = { () => mouseEnter(row,col) }>


        </div>
    )
}

export default Tile
