import React, {useState, useEffect} from 'react'
import Tile from './Tile'
import Edge from './Edge'
import ArrayOp from './StrucFuncs/ArrayOp'
import StackOp from './StrucFuncs/StackOp'
import QueueOp from './StrucFuncs/QueueOp'
import HashMapOp from './StrucFuncs/HashMapOp'
import TreeOp from './StrucFuncs/TreeOp'

function Grid(props) {
    const [grid,setGrid] = useState([])
    const [curStruc,setCurStruc] = useState([]) //all current structures in the grid
    const [chosen,setChosen] = useState({}) //id/type of Structure clicked
    const [counter,setCounter] = useState(1) //generate new  structure id
    const [lines,setLines] = useState([])

    const {op,setOp} = props 
    const type = {
        ARRAY: "Array",
        STACK: "Stack",
        QUEUE: "Queue",
        MAP: "HashMap",
        TREE:"Tree",
        GRAPH:"Graph"
    }

    useEffect(() => {
        const newGrid = initializeGrid()
        setGrid(newGrid)
    },[])

    useEffect(() => {
        let theLines=[]
         //svg building edges for all trees
        let trees = curStruc.filter(e => e.type==="tree") //all tree structures

        trees.map(e =>{
            let coor = e.tile,r,c,rr,cc,rr1,cc1,j,k

            for(let i =0;i<coor.length;i++){
                if(typeof(coor[i])==="undefined")
                     continue
                [r,c] = coor[i]
                j = 2*i +1
                let [x1,y1] = getAbsPosition(document.getElementById(`${r},${c}`))          //parent
                console.log(x1)

                if(!(typeof(coor[j]) === "undefined")){
                    [rr,cc] = coor[j]
                    let [x2,y2] = getAbsPosition(document.getElementById(`${rr},${cc}`))     //child
                    theLines.push([x1,y1,x2,y2])
                }
                k = 2*i +1

                if(!(typeof(coor[k]) === "undefined")){
                    [rr1,cc1] = coor[k]
                    let [x2,y2] = getAbsPosition(document.getElementById(`${rr1},${cc1}`))    //child
                    theines.push([x1,y1,x2,y2])


                }
            }
            console.log(lines)


        })

    },[curStruc])



    let option 

    if(op==="Array"){
        option = <ArrayOp 
            gr = {grid}
            setGr = {setGrid}
            ch = {chosen}
            st = {curStruc}
            type ={type}
            setSt = {setCurStruc}
            count = {counter} />
    }
    else if(op==="Stack"){
        option = <StackOp 
            gr = {grid}
            setGr = {setGrid}
            ch = {chosen}
            st = {curStruc}
            type ={type}
            setSt = {setCurStruc}
            count = {counter} />
    }
    else if(op==="Queue"){
        option = <QueueOp 
            gr = {grid}
            setGr = {setGrid}
            ch = {chosen}
            st = {curStruc}
            type ={type}
            setSt = {setCurStruc}
            count = {counter} />
    }
    else if(op==="HashMap"){
        option = <HashMapOp 
            gr = {grid}
            setGr = {setGrid}
            ch = {chosen}
            st = {curStruc}
            type ={type}
            setSt = {setCurStruc}
            count = {counter} />
    }
    else if(op==="Tree"){
        option = <TreeOp 
            gr = {grid}
            setGr = {setGrid}
            ch = {chosen}
            st = {curStruc}
            type ={type}
            setSt = {setCurStruc}
            count = {counter} />
    }


    return (
        <div className ="myGrid">

            {grid.map((row,rId) =>(
                 <div className = "myRow" key={rId}>

                     {row.map((tile,tId) =>(
                        <Tile
                         key ={rId+","+tId} 
                         col = {tile.col}
                         row = {tile.row}
                         sType = {tile.strType}
                         sId = {tile.strId}
                         st = {curStruc}
                         setSt = {setCurStruc}
                         ch = {chosen}
                         setCh = {setChosen}
                         gr = {grid}
                         setGr = {setGrid}
                         isP ={tile.isPicked}
                         op = {op}
                         setOp ={setOp}
                         type ={type}
                         count = {counter}
                         setCount = {setCounter}
                         txt = {tile.txt}
                         aKey ={rId+","+tId}

                         />

                     ))}
                 </div>
            ))}

            {lines.map((e,i) =>(
                <Edge key={i} x1={e[0]} y1={e[1]} x2={e[2]} y2={e[3]} />
            ))}
            {option}




        </div>
    )
}

export default Grid

const initializeGrid = () => {
    const grid = []
    for(let row = 0; row <17;row++){
        const aRow = []
        for(let col = 0; col<35;col++){
            aRow.push(createTile(col,row))
        }
        grid.push(aRow)
    }
    return grid
}

const createTile = (col,row) => {
    return{
        col,
        row,
        txt:"",
        strType: ``,
        strId: 0,
        isPicked: false,
        myKey:""
    }
}

const getAbsPosition  = (element) =>{
    console.log(element)
    var rect = element.getBoundingClientRect()
    console.log(rect)

    return [rect.left,rect.top]
 }
