                neighbor = edges[currTile][i]
                let alt = distances[currTile] + parseInt(neighbor[2],10)
                let n = neighbor[5]
                til2 = document.getElementById(myStruc[n].join())
                til2.classList.add("look-1")

                console.log(alt +"    "+distances[n]+"   "+n)
