import React, {useEffect} from 'react'


function TreeOp(props) {

    useEffect(() => {
        const newGrid = initializeGrid()
        setGrid(newGrid)
    },[])

    const {gr,setGr,ch,st,type,setSt,count} = props
    let line
    const addValue = (e) => {
        e.preventDefault()
        let value = document.getElementById("input1").value
        value = parseInt(value,10)

        const fullStruc = (st.find(e => e.id === ch.id))
        let myStruc = fullStruc.tile //array of coordinates
        let [r,c] = myStruc[0]
        const newGrid = gr.slice()

        let i
        for(i =0; i<15;){

                console.log("in there")
                if(gr[r][c].txt > value){
                    i = 2*i+1 

                    if(typeof(myStruc[i])==="undefined")
                        break
                    r = myStruc[i][0] //left child
                    c = myStruc[i][1] //left child
                }
                else{
                    i = 2*i+2
                    if(typeof(myStruc[i])==="undefined")
                        break

                    r = myStruc[i][0] //right child
                    c = myStruc[i][1] //right child
                }

            }
        if(i>14) return  //reached tree depth limit
        let newR,newC, offset = [0,4,4,2,2,2,2,1,1,1,1,1,1,1,1]
        let g = gr[r][c] //parent
        if(g.txt > value){
            newR = r +1
            newC = c - offset[i]
        }
        else{
            newR = r +1
            newC = c + offset[i]
        }
        const aTile = newGrid[newR][newC]//child
        if(typeof(aTile) == "undefined" || !(aTile.strType === "")) return //tile is already being used
        const newTile= {
            ...aTile,
            txt:`${value}`,
            strType: type.TREE,
            strId:fullStruc.id,
            myKey: g.myKey +""+newC //for unique keys
            }
        newGrid[newR][newC] = newTile
        //connecting lines
        console.log("MY PARENT ID IS: "+r+","+c)
        let {x1,y1} = getAbsPosition(document.getElementById(`#${r},${c}`))          //parent
        let {x2,y2} = getAbsPosition(document.getElementById(`#${newR},${newC}`));     //child
        line =<svg width="500" height="500"><line x1= {`${x1}`} y1={`${y1}`} x2={`${x2}`} y2={`${y2}`} stroke="red"/></svg>

        setGr(newGrid)
        const newSt = st.filter(e => !(e.id ===fullStruc.id))//list of data structures without tree
        myStruc[i]=[newR,newC]
        newSt.push({id:ch.id,tile:myStruc})
        setSt(newSt)
        console.log(st)

        //connecting lines

        }




    return (
        <div className = "controls" >
                <form onSubmit = {addValue}>
                    <input id="input1" type="number" placeholder="value" />
                    <button>Add</button>

                </form>
                {line}

                <div>
                    see items
                </div>
            </div>
    )
}

export default TreeOp

function getAbsPosition(element) {
    console.log(element)
    var rect = element.getBoundingClientRect();
    return {x:rect.left,y:rect.top}
 }
