import React from 'react'

function Tile(props) {
    const {col,row,sType,sId,st,setSt,pr,setPr,gr,setGr,isP,op,setOp,type} = props
    //console.log(col +" "+ row+" "+sType+ " "+sId)


    const stackGrid = ( size) => {
        if((row -size) >= -1){

        const newGrid = gr.slice();
        let tile,newTile;
        for(let i =0;i<size;i++){
            tile = newGrid[row-i][col];
            newTile= {
              ...tile,
              isPicked: !isP,
              strType: type.STACK
            };

            newGrid[row-i][col] = newTile;
        }
         setGr(newGrid)
        }
      }

    const arrayGrid = (size) =>{
        if((col +size) <= 45){

            const newGrid = gr.slice();
            let tile, newTile;
            for(let i =0;i<size;i++){
                tile = newGrid[row][col+i];
                newTile= {
                ...tile,
                isPicked: !isP,
                strType: type.ARRAY
                };

                newGrid[row][col+i] = newTile;
            }
            setGr(newGrid)
        }

    }

    const queueGrid = (size) => {
        if((col -size) >= -1){

            const newGrid = gr.slice();
            let tile, newTile;
            for(let i =0;i<size;i++){
                tile = newGrid[row][col-i];
                newTile= {
                ...tile,
                isPicked: !isP,
                strType: type.QUEUE

                };

                newGrid[row][col-i] = newTile;
            }
            setGr(newGrid)
        }
    }
    const mapGrid = (size) => {
        if((col +2) <= 44  && (row +size) <= 20){
            console.log("no")
            const newGrid = gr.slice();
            let tile, newTile;
            for(let i = 0; i<3;i++){
                for(let j = 0;j<size; j++){
                    tile = newGrid[row+j][col+i];
                newTile= {
                ...tile,
                isPicked: !isP,
                strType: type.MAP

                };

                newGrid[row+j][col+i] = newTile;
                }
            }
            setGr(newGrid)

        }
    }


/*
    const mouseDown = (aRow,aCol) => {
        const newAr = ch.slice()
        newAr.push({aRow,aCol})
        setCh(newAr)

        const newGrid = updateGrid(gr,row,col)
        setGr(newGrid)

        setPr(true)
    }

    const mouseLift = () => {
        setPr(false)
        setCh([])
        let ans= prompt("Size:")
        console.log(ans +"")
    }

    const mouseEnter =(aRow,aCol)=> {

        if(!pr) return 
        const newAr = ch.slice()
        newAr.push({aRow,aCol})
        setCh(newAr)

        const newGrid = updateGrid(gr,row,col)
        setGr(newGrid)
    }

