    const click =() =>{
        let size= parseInt(prompt("Size:"),10)
        if(op === "Array")
            arrayGrid(size)
        else if(op === "Stack")
            stackGrid(size)
        else if(op === "Queue")
            queueGrid(size)
        else if(op === "HashMap")
            mapGrid(size)
        else if(op === "Tree")
            console.log("nothing yet")
        else if(op === "Graph")
            console.log("nothing yet")

    }

    let extra = isP ? 'bground' : ''
    return (
        <div className = {`myTile ${extra}`} 
            onClick = {() => click()} >

        </div>
    )
}

export default Tile
