import React from 'react'

function Tile(props) {
    const {col,row,sType,sId,ch,setCh,pr,setPr} = props
    //console.log(col +" "+ row+" "+sType+ " "+sId)

    const mouseDown = (aRow,aCol) => {
        const newAr = chslice()
        newAr.push({aRow,aCol})
        setCh({cTiles:newAr,hold:true})
    }

    const mouseLift = () => {
        setCh({hold:false})
        console.log(ch.cTiles)
    }

    const mouseEnter =(aRow,aCol)=> {

        if(!ch.hold) return 
        const newAr = ch.cTiles.slice()
        newAr.push({aRow,aCol})
        setCh({cTiles:newAr})
    }

    return (
        <div className = "myTile" 
             onMouseDown = {() => mouseDown(row,col)}
             onMouseUp = {() => mouseLift()}
             onMouseEnter = { () => mouseEnter(row,col) }>


        </div>
    )
}

export default Tile
