import React from 'react'

function Tile(props) {
    const {col,row,sType,sId,st,setSt,ch,setCh,gr,setGr,isP,op,setOp,type,count,setCount,txt} = props
    //console.log(col +" "+ row+" "+sType+ " "+sId)


    const stackGrid = ( size) => {
        if((row -size) >= -1){

        const newGrid = gr.slice();
        let tile,newTile;
        for(let i =0;i<size;i++){
            tile = newGrid[row-i][col];
            newTile= {
              ...tile,
              isPicked: !isP,
              strType: type.STACK
            };

            newGrid[row-i][col] = newTile;
        }
         setGr(newGrid)
        }
      }

    const arrayGrid = (size) =>{
        //setOp("")
        if((col +size) <= 45){

            const newGrid = gr.slice()
            let tile, newTile,acol,coor =[]


            for(let i =0;i<size;i++){
                tile = newGrid[row][col+i]
                newTile= {
                ...tile,
                isPicked: !isP,
                strType: type.ARRAY,
                strId:count
                };

                newGrid[row][col+i] = newTile
                acol =col+i
                coor.push([row,acol])
            }
            setGr(newGrid)
            let newCount = count +1;
            setCount(newCount)

            const newSt = st.slice() //copy list of data structues
            newSt.push({id: count, tile: coor})
            setSt(newSt)
            console.log(st)
        }

    }

    const queueGrid = (size) => {
        if((col -size) >= -1){

            const newGrid = gr.slice();
            let tile, newTile;
            for(let i =0;i<size;i++){
                tile = newGrid[row][col-i];
                newTile= {
                ...tile,
                isPicked: !isP,
                strType: type.QUEUE

                };

                newGrid[row][col-i] = newTile;
            }
            setGr(newGrid)
        }
    }
    const mapGrid = (size) => {
        if((col +2) <= 44  && (row +size) <= 20){
            console.log("no")
            const newGrid = gr.slice();
            let tile, newTile;
            for(let i = 0; i<3;i++){
                for(let j = 0;j<size; j++){
                    tile = newGrid[row+j][col+i]
                newTile= {
                ...tile,
                isPicked: !isP,
                strType: type.MAP

                }

                newGrid[row+j][col+i] = newTile;
                }
            }
            setGr(newGrid)

        }
    }


