        const newAr = ch.slice()
        newAr.push({aRow,aCol})
        setCh(newAr)

        const newGrid = updateGrid(gr,row,col)
        setGr(newGrid)

        setPr(true)
    }

    const mouseLift = () => {
        setPr(false)
        setCh([])
        let ans= prompt("Size:")
        console.log(ans +"")
    }

    const mouseEnter =(aRow,aCol)=> {

        if(!pr) return 
        const newAr = ch.slice()
        newAr.push({aRow,aCol})
        setCh(newAr)

        const newGrid = updateGrid(gr,row,col)
