    const click =() =>{
        if(sId ===0){
            if(op !== ""){
                let size= parseInt(prompt("Size:"),10)
                if(op === "Array")
                    arrayGrid(size)
                else if(op === "Stack")
                    stackGrid(size)
                else if(op === "Queue")
                    queueGrid(size)
                else if(op === "HashMap")
                    mapGrid(size)
                else if(op === "Tree")
                    console.log("nothing yet")
                else if(op === "Graph")
                    console.log("nothing yet")
            }
        }
        else{
            setCh(sId)
        }
    }

    const extra = sId === ch ? 'highlight' : ''
    const main = sType === type.Arra
    return (
        <div className = {`myTile ${extra}`} 
            onClick = {() => click()} >
                {txt}
        </div>
    )
}

export default Tile
