import React, {useState, useEffect} from 'react'
import Tile from './Tile'

function Grid() {
    const [grid,setGrid] = useState([])
    const [curStruc,setCurStruc] = useState([]) //all current structures in the grid
    const [chosen,setChosen] = useState({cTiles:[],hold:false}) //all blocks currently clicked/hovered

    useEffect(() => {
        const newGrid = initializeGrid()
        setGrid(newGrid)
    },[])

    const mouseDown = (aRow,aCol) => {
        const newAr = chosen.splice()
        newAr.push({aRow,aCol})
        setChosen({cTiles:newAr,hold:true})
    }

    const mouseLift = () => {
        //pop up
        setChosen({hold:false})
    }

    const mouseEnter =()=> {
        if(!chosen.hold) return 

    }







    return (
        <div className ="myGrid">

            {grid.map((row,rId) =>(
                 <div className = "myRow" key={rId}>
                     {row.map((tile,tId) =>(
                        <Tile key ={tId} col = {tile.col}/>

                     ))}
                 </div>
            ))}

        </div>
    )
}

export default Grid

const initializeGrid = () => {
    const grid = []
    for(let row = 0; row <20;row++){
        const aRow = []
        for(let col = 0; col<40;col++){
            aRow.push(createTile(col,row))
        }
        grid.push(aRow)
    }
    return grid
}

const createTile = (col,row) => {
    return{
        col,
        row,
        strType: null,
        strId: null,

    }
}
