import React from 'react'


function TreeOp(props) {

    const {gr,setGr,ch,st,type,setSt,count} = props

    const addValue = (e) => {
        e.preventDefault()
        let value = document.getElementById("input1").value
        value = parseInt(value,10)

        //reinsert coordinates of tree in each add



        const fullStruc = (st.find(e => e.id === ch.id))
        const myStruc = fullStruc.tile //array of coordinates
        const newGrid = gr.slice()
        let [r,c] = myStruc[0]
        console.log(myStruc)

        let tile = newGrid[r][c] //root tile on grid

        r=tile.row

        for(let i =0; i<15;){
            console.log(i)
            if(typeof(tile) ==="object"  && !(tile.txt ==="")){
                pParent = tile

                if(tile.txt > value){
                    i = 2*i+1
                    tile = myStruc[i] //left child
                }
                else{
                    i = 2*i+2
                    tile = myStruc[i] //right child
                }}
            else{
                let newR,newC, offset = [0,4,4,2,2,2,2,1,1,1,1,1,1,1,1]

                if(pParent.txt > value){
                    newR = pParent[i][0] +1
                    newC = pParent[i][1] - offset[i]
                }
                else{
                    newR = pParent[i][0] +1
                    newC = pParent[i][1] + offset[i]
                }
                console.log(pParent)
                const aTile = newGrid[newR][newC]
                const newTile= {
                    ...aTile,
                    txt:`${value}`,
                    strType: type.TREE,
                    strId:fullStruc.id

                    }
                newGrid[newR][newC] = newTile
                setGr(newGrid)
                const newSt = st.filter(e => e.id ===fullStruc.id)//list of data structures without tree
                myStruc[i]=[newR,newC]
                newSt.push({id: count, tile: myStruc})
                setSt(newSt)
                break
                }
            }
        }




    return (
        <div className = "controls" >
                <form onSubmit = {addValue}>
                    <input id="input1" type="number" placeholder="value" />
                    <button>Add</button>

                </form>


                <div>
                    see items
                </div>
            </div>
    )
}

export default TreeOp
