import React from 'react'

function Tile(props) {
    const {col,row,sType,sId,st,setSt,ch,setCh,gr,setGr,isP,op,setOp,type,count,setCount,txt} = props


    const stackGrid = ( size) => {
        if((row -size) >= -1){

        const newGrid = gr.slice();
        let tile,newTile,arow,coor =[]
        for(let i =0;i<size;i++){
            arow =row-i
            tile = newGrid[arow][col];
            newTile= {
              ...tile,
              isPicked: !isP,
              strType: type.STACK,
              strId:count
            };

            newGrid[arow][col] = newTile
            coor.push([arow,col])
        }
        setGr(newGrid)
        let newCount = count +1;
        setCount(newCount)

        const newSt = st.slice() //copy list of data structues
        newSt.push({id: count, tile: coor})
        setSt(newSt)
        }
      }

    const arrayGrid = (size) =>{
        //setOp("")
        if((col +size) <= 45){

            const newGrid = gr.slice()
            let tile, newTile,acol,coor =[]


            for(let i =0;i<size;i++){
                tile = newGrid[row][col+i]
                newTile= {
                ...tile,
                isPicked: !isP,
                strType: type.ARRAY,
                strId:count
                };

                newGrid[row][col+i] = newTile
                acol =col+i
                coor.push([row,acol])
            }
            setGr(newGrid)
            let newCount = count +1;
            setCount(newCount)

            const newSt = st.slice() //copy list of data structues
            newSt.push({id: count, tile: coor})
            setSt(newSt)
            console.log(st)
        }

    }

    const queueGrid = (size) => {
        if((col -size) >= -1){

            const newGrid = gr.slice();
            let tile, newTile,acol,coor =[]
            for(let i =0;i<size;i++){
                acol = col-i
                tile = newGrid[row][acol];
                newTile= {
                ...tile,
                isPicked: !isP,
                strType: type.QUEUE,
                strId:count

                };

                newGrid[row][acol] = newTile;
                coor.push([row,acol])
            }
            setGr(newGrid)
            let newCount = count +1;
            setCount(newCount)

            const newSt = st.slice() //copy list of data structues
            newSt.push({id: count, tile: coor})
            setSt(newSt)
        }
    }
    const mapGrid = (size) => {
        if((col +2) <= 44  && (row +size) <= 20){
            const newGrid = gr.slice();
            let tile, newTile,coor =[],arr
            for(let i = 0; i<size;i++){
                arr =[]
                for(let j = 0;j<3; j++){
                    tile = newGrid[row+i][col+j]
                    newTile= {
                    ...tile,
                    isPicked: !isP,
                    strType: type.MAP, 
                    strId:count

                    }
                newGrid[row+i][col+j] = newTile;

                arr.push([row,acol]) //pushes for single key/value


                }
                coor.push(arr)
            }
            setGr(newGrid)
            let newCount = count +1;
            setCount(newCount)

            const newSt = st.slice() //copy list of data structues
            newSt.push({id: count, tile: coor})
            setSt(newSt)

        }
    }




    const click =() =>{
        if(sId ===0){
            if(op !== ""){
                let size= parseInt(prompt("Size:"),10)
                if(op === "Array")
                    arrayGrid(size)
                else if(op === "Stack")
                    stackGrid(size)
                else if(op === "Queue")
                    queueGrid(size)
                else if(op === "HashMap")
                    mapGrid(size)
                else if(op === "Tree")
                    console.log("nothing yet")
                else if(op === "Graph")
                    console.log("nothing yet")
            }
        }
        else{
            setCh({id:sId , type:sType})
        }
    }

    const extra = sId === ch.id ? 'highlight' : ''
    const main = sType === ``? ``:
                sType ===type.ARRAY ? `array` :
                sType ===type.STACK ? `stack` :
                sType ===type.QUEUE ? `queue` :
                sType ===type.MAP ? `map` :
                sType ===type.TREE ? `tree` :`graph` 
    return (
        <div className = {` myTile ${main} ${extra}`} 
            onClick = {() => click()} >
                {txt}
        </div>
    )
}

export default Tile
