import React from 'react'

function Tile(props) {
    const {col,row,sType,sId,ch,setCh} = props

    const mouseDown = (aRow,aCol) => {
        const newAr = ch.slice()
        newAr.push({aRow,aCol})
        setCh({cTiles:newAr,hold:true})
    }

    const mouseLift = () => {
        //pop up
        setCh({hold:false})
    }

    const mouseEnter =(aRow,aCol)=> {
        console.log(aRow +" ")

        if(!ch.hold) return 
        const newAr = ch.slice()
        newAr.push({aRow,aCol})
        setCh({cTiles:newAr})
    }

    return (
        <div className = "myTile" 
             /*onMouseDown = {mouseDown(row,col)}
             onMouseUp = {mouseLift()}*/
             onMouseEnter = {mouseEnter(row,col)}>


        </div>
    )
}

export default Tile
