import React, {useState, useEffect} from 'react'
import Tile from './Tile'

function Grid() {
    const [grid,setGrid] = useState([])
    const [curStruc,setCurStruc] = useState([]) //all current structures in the grid
    const [chosen,setChosen] = useState([]) //all blocks currently clicked/hovered
    const [press, setPress] = useState(false) //is mouse click being held
    const {op,setOp} = props


    useEffect(() => {
        const newGrid = initializeGrid()
        setGrid(newGrid)
    },[])


    return (
        <div className ="myGrid">

            {grid.map((row,rId) =>(
                 <div className = "myRow" key={rId}>
                     {row.map((tile,tId) =>(
                        <Tile
                         key ={tId} 
                         col = {tile.col}
                         row = {tile.row}
                         sType = {tile.strType}
                         sId = {tile.strId}
                         ch = {chosen}
                         setCh = {setChosen}
                         pr = {press}
                         setPr = {setPress}
                         gr = {grid}
                         setGr = {setGrid}
                         isP ={tile.isPicked}

                         />

                     ))}
                 </div>
            ))}

        </div>
    )
}

export default Grid

const initializeGrid = () => {
    const grid = []
    for(let row = 0; row <20;row++){
        const aRow = []
        for(let col = 0; col<45;col++){
            aRow.push(createTile(col,row))
        }
        grid.push(aRow)
    }
    return grid
}

const createTile = (col,row) => {
    return{
        col,
        row,
        strType: null,
        strId: null,
        isPicked: false
    }
}
