import React, {useState, useEffect} from 'react'
import Tile from './Tile'
import ArrayOp from './StrucFuncs/ArrayOp'
import StackOp from './StrucFuncs/StackOp'

function Grid(props) {
    const [grid,setGrid] = useState([])
    const [curStruc,setCurStruc] = useState([]) //all current structures in the grid
    const [chosen,setChosen] = useState({}) //id/type of Structure clicked
    const [counter,setCounter] = useState(1) //generate new id

    const {op,setOp} = props 

    const type = {
        ARRAY: "array",
        STACK: "stack",
        QUEUE: "queue",
        MAP: "map",
        TREE:"tree",
        GRAPH:"graph"
    }


    useEffect(() => {
        const newGrid = initializeGrid()
        setGrid(newGrid)
    },[])


    return (
        <div className ="myGrid">

            {grid.map((row,rId) =>(
                 <div className = "myRow" key={rId}>
                     {row.map((tile,tId) =>(
                        <Tile
                         key ={tId} 
                         col = {tile.col}
                         row = {tile.row}
                         sType = {tile.strType}
                         sId = {tile.strId}
                         st = {curStruc}
                         setSt = {setCurStruc}
                         ch = {chosen}
                         setCh = {setChosen}
                         gr = {grid}
                         setGr = {setGrid}
                         isP ={tile.isPicked}
                         op = {op}
                         setOp ={setOp}
                         type ={type}
                         count = {counter}
                         setCount = {setCounter}
                         txt = {tile.text}

                         />

                     ))}
                 </div>
            ))}

                <ArrayOp 
                    gr = {grid}
                    setGr = {setGrid}
                    ch = {chosen}
                    st = {curStruc}

                    />
                <StackOp 
                    gr = {grid}
                    setGr = {setGrid}
                    ch = {chosen}
                    st = {curStruc}

                    />



        </div>
    )
}

export default Grid

const initializeGrid = () => {
    const grid = []
    for(let row = 0; row <20;row++){
        const aRow = []
        for(let col = 0; col<45;col++){
            aRow.push(createTile(col,row))
        }
        grid.push(aRow)
    }
    return grid
}

const createTile = (col,row) => {
    return{
        col,
        row,
        txt:"",
        strType: ``,
        strId: 0,
        isPicked: false,

    }
}

const arrayOpt = <div className ="arraOpt"></div>
